#!/bin/bash

# Setup script for Vertex AI environment variables
# Fill in the values below and run this script

echo "Setting up Vertex AI environment variables for Supabase..."

# TODO: Replace with your actual base64-encoded service account key
GCP_SERVICE_ACCOUNT_KEY_BASE64=""

# TODO: Replace with your actual Google Cloud Project ID
GCP_PROJECT_ID="white-proxy-472615-c9"

# TODO: Replace with your preferred region (optional, defaults to us-central1)
GCP_LOCATION="us-central1"

# Set the environment variables using Supabase CLI
echo "Setting GCP_SERVICE_ACCOUNT_KEY_BASE64..."
supabase secrets set GCP_SERVICE_ACCOUNT_KEY_BASE64="$GCP_SERVICE_ACCOUNT_KEY_BASE64"

echo "Setting GCP_PROJECT_ID..."
supabase secrets set GCP_PROJECT_ID="$GCP_PROJECT_ID"

echo "Setting GCP_LOCATION..."
supabase secrets set GCP_LOCATION="$GCP_LOCATION"

echo "Environment variables set successfully!"
echo "You can verify them with: supabase secrets list"
